if (obj) {
    obj->fields.push_back(fld =new Field({name}, 
                                     {type},safe({default}),{indexed},{unique}));
} else if (rel) {
    rel->fields.push_back(fld = new Field({name}, 
                         {type},safe({default}),{indexed}, {unique}));
}

idx = new Index({unique});
if (obj) 
    obj->indices.push_back(idx);
else if (rel) 
    rel->indices.push_back(idx);

